import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class AuthserviceService {
  private apiUrl = 'http://localhost:3000'; // Replace this with your server's URL

  constructor(private http: HttpClient) { }

  loginuser(user: any) {
    return this.http.post<any>(`${this.apiUrl}/login`, user);
  }

  logout() {
    // Remove token from local storage on logout
    localStorage.removeItem('token');
  }

  getToken(): string | null {
    return localStorage.getItem('token');
  }

  // Add the getUserRole() method to get the user's role
  getUserRole(): string | null {
    // Assuming the role is stored in the JWT token as 'role'
    const token = this.getToken();
    if (token) {
      // Parse the JWT token to get the payload
      const payload = JSON.parse(atob(token.split('.')[1]));
      // Extract and return the role from the payload
      return payload.role;
    }
    return null;
  }
}
