import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import{auth}

@Injectable({
  providedIn: 'root'
})
export class AuthserviceService {
  private apiUrl = 'http://localhost:3000'; // Replace this with your server's URL

  constructor(private http: HttpClient) { }

  loginuser(user: any) {
    return this.http.post<any>(`${this.apiUrl}/login`, user);
  }

  logout() {
    // Remove token from local storage on logout
    localStorage.removeItem('token');
  }

  getToken(): string | null {
    return localStorage.getItem('token');
  }
  isAdminRole(): boolean {
    const userRole = this.authService.role; // Assuming the role is stored in a variable named 'role'
    return userRole === 'adminRoleValue';
  }

}
